using namespace std;
struct point{
    int x,y;
};

struct line{
    point s,t;
};
struct bec{
    int x,y;
};

double suichoku(point p, line l){
    bec p1p,p1p2;
    p1p.x=p.x-l.s.x; p1p.y=p.y-l.s.y;
    p1p2.x=l.t.x-l.s.x; p1p2.y=l.t.y-l.s.y;
    double c,dp,dp2,k,ansx,ansy;
    dp=sqrt((double)p1p.x*(double)p1p.x+(double)p1p.y*p1p.y);
    dp2=sqrt((double)p1p2.x*(double)p1p2.x+(double)p1p2.y*(double)p1p2.y);
    c=((double)p1p.x*(double)p1p2.x+(double)p1p.y*(double)p1p2.y)/dp*dp2;
    k=dp*c;
    ansx=l.s.x+k*(p1p2.x/dp2);
    ansy=l.s.y+k*(p1p2.y/dp2);
    return sqrt((p.x-ansx)*(p.x-ansx)+(p.y-ansy)*(p.y-ansy));

}

void solve(line l, line m){
    double d1,d2,w,ansx,ansy;
    d1=suichoku(l.s,m);
    d2=suichoku(l.t,m);
    w=d1/(d1+d2);
    bec lp;
    lp.x=l.t.x-l.s.x; lp.y=l.t.y-l.s.y;
    ansx=l.s.x+lp.x*w;
    ansy=l.s.y+lp.y*w;

    printf("%.8f %.8f\n",ansx,ansy);

}

int main(){
    int x0,x1,x2,x3,y0,y1,y2,y3,n;
    cin>>n;
    for(int i=0;i<n;i++){
        cin>>x0>>y0>>x1>>y1>>x2>>y2>>x3>>y3;
        point s1,s2,t1,t2;
        line l,m;
        s1.x=x0; s1.y=y0;
        t1.x=x1; t1.y=y1;
        s2.x=x2; s2.y= y2;
        t2.x=x3; t2.y=y3;
        l.s=s1; l.t=t1;
        m.s=s2; m.t=t2;
        solve(l,m);
    }
}

