inline int signum(double x) { return (abs(x) < EPS) ? 0 : (x > 0) ? 1 : -1; }

//-----definition-----
typedef complex<double> P;

//-----standard-----
pair<P, P> norm_vec(P a) {
    return make_pair(a * P(0, 1), a * P(0, -1));
}

P unit_vec(P a) { a / abs(a); }

double dot(P a, P b) {
    return a.real() * b.real() + a.imag() * b.imag();
}

double cross(P a, P b) {
    return a.real() * b.imag() - a.imag() * b.real();
}

int ccw(P a, P b, P c) {
    b -= a;
    c -= a;
    int sign = signum(cross(b, c));
    if (sign != 0) return sign;
    if (signum(dot(b, c)) < 0) return 2;//left
    if (abs(b) < abs(c)) return -2;//right
    return 0; //mid
}

//-----line-----
bool is_paral(P a1, P a2, P b1, P b2) {
    return signum(cross(a2 - a1, b2 - b1)) == 0;
}

bool is_orth(P a1, P a2, P b1, P b2) {
    return signum(dot(a2 - a1, b2 - b1)) == 0;
}

bool is_p_on_li(P a, P b, P c) {
    b -= a;
    c -= a;
    return signum(cross(b, c)) == 0;
}

bool is_p_on_li_s(P a, P b, P c) {
    return abs(c - a) + abs(b - c) < abs(b - a) + EPS;
}

double dis_p_li(P a, P b, P c) {
    b -= a;
    c -= a;
    return abs(cross(b, c)) / abs(b);
}

double dis_p_li_ss(P a, P b, P c) {
    P x1 = b - a;
    P y1 = c - a;
    P x2 = a - b;
    P y2 = c - b;
    if (cross(x1, y1) < EPS) return abs(y1);
    if (cross(x2, y2) < EPS) return abs(y2);
    return dis_p_li(a, b, c);
}

bool is_inter_li_ss(P a1, P a2, P b1, P b2) {
    return (ccw(a1, a2, b1) * ccw(a1, a2, b2) <= 0) && (ccw(b1, b2, a1) * ccw(b1, b2, a2) <= 0);

}

P inter_li_ss(P a1, P a2, P b1, P b2) {
    double d1 = abs(cross(b2 - b1, a1 - b1));
    double d2 = abs(cross(b2 - b1, a2 - b1));
    double t = d1 / (d1 + d2);
    return a1 + (a2 - a1) * t;
}

