	{
		return OwnVector2(x*v, y*v);
	}
};

OwnVector2 CalcCrossPoint(OwnVector2 p1, OwnVector2 p2, OwnVector2 p3, OwnVector2 p4)
{
	OwnVector2 p12, p13, p14, p34, p31, p32;
	p12 = p2 - p1; p13 = p3 - p1; p14 = p4 - p1; p34 = p4 - p3; p31 = p1 - p3; p32 = p2 - p3;

	double ksi, delta;

	ksi = (p14.x*p34.y - p14.y*p34.x);
	delta = (p12.x*p34.y - p12.y*p34.x);

	return p1 + p12*(ksi / delta);
}

