template <typename T>
struct Point {
    T x, y;

    Point() = default;
    Point(T x, T y) : x(x), y(y) {}

    template <typename U>
    Point(const Point<U> &rhs) : x(rhs.x), y(rhs.y) {}

    Point<T> operator+(const Point<T> &rhs) const {
        return Point(x + rhs.x, y + rhs.y);
    }

    Point<T> operator-(const Point<T> &rhs) const {
        return Point(x - rhs.x, y - rhs.y);
    }

    T operator*(const Point<T> &rhs) const {
        return x * rhs.x + y * rhs.y;
    }

    T operator%(const Point<T> &rhs) const {
        return x * rhs.y - y * rhs.x;
    }
};

struct Line{
    // ax+by+c=0
    int64_t a,b,c;
    double angle;
    Point<int64_t> pa,pb;
    Line():a(0),b(0),c(0),angle(0),pa(),pb(){}
    Line(Point<int64_t> pa, Point<int64_t> pb):a(pa.y-pb.y),b(pb.x-pa.x),c(pa%pb),angle(atan2(-a,b)),pa(pa),pb(pb){}
};

Point<double> intersect(Line la,Line lb){
    if(la.a*lb.b == la.b*lb.a) throw;
    int64_t bot=-la.a*lb.b+la.b*lb.a;
    return Point<double>(1.0 * (-la.b*lb.c+la.c*lb.b) / bot,1.0 * (la.a*lb.c-la.c*lb.a) / bot);
}

