class Vector{
    public static final double EPS = 1e-10;
    public static final int COUNTER_CLOCKWISE=1;
    public static final int CLOCKWISE=-1;
    public static final int ONLINE_BACK=2;
    public static final int ONLINE_FRONT=-2;
    public static final int ON_SEGMENT=0;

    public double x,y;

    public Vector(){}
    public Vector(double x, double y){this.x=x;this.y=y;}

    public Vector plus(Vector p){return new Vector(this.x+p.x, this.y+p.y);}
    public Vector minus(Vector p){return new Vector(this.x-p.x, this.y-p.y);}
    public Vector multiple(double a){return new Vector(this.x*a, this.y*a);}
    public Vector divide(double a){return new Vector(this.x/a, this.y/a);}

    public double abs(){return Math.sqrt(this.norm());}
    public double norm(){return x*x+y*y;}


    public static double dot(Vector a, Vector b){
        return a.x*b.x+a.y*b.y;
    }

    public static double cross(Vector a, Vector b){
        return a.x*b.y-a.y*b.x;
    }

    //point.projectで垂線の足の座標
    public Vector project(Vector seg1, Vector seg2){
        Vector seg = seg2.minus(seg1);
        Vector relPoint = this.minus(seg1);
        double t = dot(seg, relPoint)/seg.norm();
        return seg1.plus(seg.multiple(t));
    }

    //point.reflectionで対称点
    public Vector reflection(Vector seg1, Vector seg2){
        Vector projected = this.project(seg1, seg2);
        Vector ashi = projected.minus(this);
        return this.plus(ashi.multiple(2));
    }

    //p0が始点
    public static int ccw(Vector p0, Vector p1, Vector p2){
        Vector s1 = p1.minus(p0);
        Vector s2 = p2.minus(p0);

        double cross = Vector.cross(s1, s2);
        double dot = Vector.dot(s1, s2);

        if(cross>0){return COUNTER_CLOCKWISE;}
        if(cross<0){return CLOCKWISE;}
        if(dot<0){return ONLINE_BACK;}
        if(s1.abs()<s2.abs()){return ONLINE_FRONT;}
        return ON_SEGMENT;
    }

    public static Boolean intersect(Vector p0, Vector p1, Vector p2, Vector p3){
        if(ccw(p0, p1, p2)*ccw(p0, p1, p3)<=0 && ccw(p2, p3, p0)*ccw(p2, p3, p1)<=0){
            return true;
        }else{
            return false;
        }
    }

    //平行四辺形から幾何的に
    public static Vector getCrossPoint(Vector p0, Vector p1, Vector p2, Vector p3){
        Vector base = p3.minus(p2);
        double d1 = Math.abs(cross(base, p0.minus(p2)));
        double d2 = Math.abs(cross(base, p1.minus(p2)));
        double t = d1/(d1+d2);
        return p0.plus((p1.minus(p0)).multiple(t));
    }
}

import java.io.IOException;
import java.io.InputStream;
import java.text.DecimalFormat;
import java.util.NoSuchElementException;

public class Main {
	static FastScanner sc = new FastScanner();

	public static void main(String[] args) {
		int q = sc.nextInt();

		StringBuilder ans = new StringBuilder();
		for(int i=0; i<q; i++) {
			double x0 = sc.nextDouble();
			double y0 = sc.nextDouble();
			double x1 = sc.nextDouble();
			double y1 = sc.nextDouble();
			double x2 = sc.nextDouble();
			double y2 = sc.nextDouble();
			double x3 = sc.nextDouble();
			double y3 = sc.nextDouble();

			double a1 = (y1 - y0) / (x1 - x0);
			double a2 = (y3 - y2) / (x3 - x2);
			double b1 = y0 - a1 * x0;
			double b2 = y2 - a2 * x2;

			double x;
			double y;
			if(x1 == x0) {
				x = x0;
				y = a2 * x + b2;
			} else if(x3 == x2) {
				x = x2;
				y = a1 * x + b1;
			} else {
				x = (b1 - b2)/(a2 - a1);
				y = a1 * x + b1;
			}
			DecimalFormat df = new DecimalFormat("0.00000000");
			ans.append(df.format(x));
			ans.append(" ");
			ans.append(df.format(y));
			ans.append("\n");
		}
		System.out.print(ans);
	}

	static class FastScanner {
        private final InputStream in = System.in;
        private final byte[] buffer = new byte[1024];
        private int ptr = 0;
        private int buflen = 0;
        private boolean hasNextByte() {
            if(ptr < buflen) {
                return true;
            } else {
                ptr = 0;
                try {
                    buflen = in.read(buffer);
                } catch(IOException e) {
                    e.printStackTrace();
                }
                if(buflen <= 0) {
                    return false;
                }
            }
            return true;
        }
        private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}
        private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
        private void skipUnprintable() { while(hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;}
        public boolean hasNext() { skipUnprintable(); return hasNextByte();}
        public String next() {
            if (!hasNext()) throw new NoSuchElementException();
            StringBuilder sb = new StringBuilder();
            int b = readByte();
            while(isPrintableChar(b)) {
                sb.appendCodePoint(b);
                b = readByte();
            }
            return sb.toString();
        }
        public long nextLong() {
            return Long.parseLong(next());
        }
        public int nextInt(){
            return Integer.parseInt(next());
        }
        public double nextDouble(){
            return Double.parseDouble(next());
        }
    }
}

import java.awt.geom.Line2D;
import java.awt.geom.Point2D;
import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	public static void main(String[] args) throws NumberFormatException, IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

		int n = Integer.parseInt(br.readLine());


		for(int i = 0; i < n; i++){
			String[] tmpArray = br.readLine().split(" ");

			Point2D[] points = new Point2D[4];

			for(int j = 0; j < 4; j++){
				int x = Integer.parseInt(tmpArray[j*2]);
				int y = Integer.parseInt(tmpArray[j*2 + 1]);

				points[j] = new Point2D.Double(x, y);
			}

			Line2D[] lines = new Line2D[2];
			for(int j = 0; j < 2; j++){
				lines[j] = new Line2D.Double(points[j*2], points[j*2 + 1]);
			}

			Point2D result = crossPoint(lines[0], lines[1]);

			System.out.printf("%.8f %.8f\n", result.getX(), result.getY());
		}
	}

	static Point2D crossPoint(Line2D l1, Line2D l2){
		if(!l1.intersectsLine(l2)){
			return null;
		}

		//l1がx軸に垂直
		if(l1.getX1() == l1.getX2()){
			double a = (l2.getY2() - l2.getY1())/(l2.getX2() - l2.getX1());

			return new Point2D.Double(l1.getX1(), a*(l1.getX1() - l2.getX1()) + l2.getY1());
		}
		//l2がx軸に垂直
		else if(l2.getX1() == l2.getX2()){
			double a = (l1.getY2() - l1.getY1())/(l1.getX2() - l1.getX1());

			return new Point2D.Double(l2.getX1(), a*(l2.getX1() - l1.getX1()) + l1.getY1());
		}
		else {
			double a1 = (l1.getY2() - l1.getY1())/(l1.getX2() - l1.getX1());
			double a2 = (l2.getY2() - l2.getY1())/(l2.getX2() - l2.getX1());
			double b1 = -a1*l1.getX1() + l1.getY1();
			double b2 = -a2*l2.getX1() + l2.getY1();

			double x = (b2 - b1)/(a1 - a2);
			double y = a1*x + b1;

			return new Point2D.Double(x, y);

		}

	}

}

