	class Vector2
	{
		public Vector2 ( decimal x, decimal y )
		{
			this.X = x;
			this.Y = y;
		}

		public decimal X { private set; get; }
		public decimal Y { private set; get; }

		public static Vector2 operator + ( Vector2 ob1, Vector2 ob2 )
		{
			return new Vector2 (ob1.X + ob2.X, ob1.Y + ob2.Y);
		}
		public static Vector2 operator - ( Vector2 ob1, Vector2 ob2 )
		{
			return new Vector2 (ob1.X - ob2.X, ob1.Y - ob2.Y);
		}
		public static Vector2 operator * ( Vector2 ob, decimal scalar )
		{
			return new Vector2 (ob.X * scalar, ob.Y * scalar);
		}

		public static decimal CrossProduct ( Vector2 line1, Vector2 line2 )
		{
			return line1.X * line2.Y - line1.Y * line2.X;
		}

		public static Vector2 CrossPoint ( Vector2 line1Point1, Vector2 line1Point2, Vector2 line2Point1, Vector2 line2Point2 )
		{
			Vector2 baseLine = line2Point2 - line2Point1;

			Vector2 line1 = line1Point1 - line2Point1;
			Vector2 line2 = line1Point2 - line2Point1;

			decimal distance1 = Math.Abs (Vector2.CrossProduct (baseLine, line1));
			decimal distance2 = Math.Abs (Vector2.CrossProduct (baseLine, line2));

			decimal ratio = distance1 / (distance1 + distance2);

			return line1Point1 + (line1Point2 - line1Point1) * ratio;
		}
	}
}

