package main

import (
	"bufio"
	"fmt"
	"os"
	"strconv"
)

var scanner = bufio.NewScanner(os.Stdin)

func nextString() string {
	scanner.Scan()
	return scanner.Text()
}

func nextInt() int {
	n, err := strconv.Atoi(nextString())
	if err != nil {
		fmt.Printf("strconv.Atoi failed: %v\n", err)
	}
	return n
}

func nextFloat64() (float64, error) {
	return strconv.ParseFloat(nextString(), 64)
}

func min(x, y float64) float64 {
	if x < y {
		return x
	}
	return y
}

func max(x, y float64) float64 {
	if x > y {
		return x
	}
	return y
}

type Point struct {
	x float64
	y float64
}

func sub(l, r *Point) *Point {
	s := Point{
		x: l.x - r.x,
		y: l.y - r.y,
	}
	return &s
}

func crossProd(l, r *Point) float64 {
	return l.x*r.y - r.x*l.y
}

func direction(pi, pj, pk *Point) float64 {
	return crossProd(sub(pk, pi), sub(pj, pi))
}

func readPoint() *Point {
	x, _ := nextFloat64()
	y, _ := nextFloat64()
	point := Point{
		x: x,
		y: y,
	}
	return &point
}

func coef(p1, p2 *Point) (float64, float64, float64) {
	return p1.y - p2.y, -(p1.x - p2.x), p1.y*p2.x - p1.x*p2.y
}

func main() {
	scanner.Split(bufio.ScanWords)

	nQueries := nextInt()

	for iQuery := 0; iQuery < nQueries; iQuery++ {
		p0 := readPoint()
		p1 := readPoint()
		p2 := readPoint()
		p3 := readPoint()

		a, b, e := coef(p0, p1)
		c, d, f := coef(p2, p3)
		det := a*d - b*c
		x := (d*e - b*f) / det
		y := (a*f - c*e) / det
		fmt.Println(x, y)
	}
}

