		}

		s1 := complex(xx[1]-xx[0], yy[1]-yy[0])
		_, th := cmplx.Polar(s1)
		s2 := rotate(complex(xx[2]-xx[0], yy[2]-yy[0]), -th)
		s3 := rotate(complex(xx[3]-xx[0], yy[3]-yy[0]), -th)
		ans := rotate(complex(real(s2)+(real(s3)-real(s2))*math.Abs(imag(s2))/(math.Abs(imag(s2))+math.Abs(imag(s3))), 0), th)
		fmt.Fprintln(writer, fmt.Sprintf("%.10f %.10f", real(ans)+xx[0], imag(ans)+yy[0]))
	}
