def crossing_point(p0, p1, p2, p3):
    x0, y0 = p0
    x1, y1 = p1
    x2, y2 = p2
    x3, y3 = p3

    if x0 != x1 and x2 != x3:
        a0 = (y0 - y1) / (x0 - x1)
        b0 = (x1*y0 - y1*x0) / (x1 - x0)
        a1 = (y2 - y3) / (x2 - x3)
        b1 = (x3*y2 - y3*x2) / (x3 - x2)
        return (b0-b1) / (a1-a0), (a1*b0-a0*b1) / (a1-a0)
    elif x0 != x1:
        a0 = (y0 - y1) / (x0 - x1)
        b0 = (x1*y0 - y1*x0) / (x1 - x0)
        return x2, a0 * x2 + b0
    elif x2 != x3:
        a1 = (y2 - y3) / (x2 - x3)
        b1 = (x3*y2 - y3*x2) / (x3 - x2)
        return x0, a1 * x0 + b1
    else:
        raise ValueError('p0p1 and p2p3 is parallel')


