        s1 = Segment(Point(x0, y0), Point(x1, y1))
        s2 = Segment(Point(x2, y2), Point(x3, y3))
        ans.append(getCrossPoint(s1, s2))

    for v in ans:
        print('{0:.10f} {1:.10f}'.format(v.x, v.y))

