using namespace std;

const double ESP = 1e-8;

class Point
{
public:
	double x, y;

	Point(double x = 0, double y = 0) : x(x), y(y) {}

	Point operator + (Point a) { return Point(x + a.x, y + a.y); }
	Point operator - (Point a) { return Point(x - a.x, y - a.y); }
	Point operator * (double a) { return Point(x * a,y * a); }
	Point operator / (double a) { return Point(x / a, y / a); }

	double abs() { return sqrt(norm()); }
	double norm() { return x * x + y * y; }
};

typedef Point Vector;

struct Segment
{
	Point p1, p2;
};

typedef Segment Line;

double dot(Vector a, Vector b)
{
	return a.x * b.x + a.y * b.y;
}

double cross(Vector a, Vector b)
{
	return a.x * b.y - a.y * b.x;
}

int ccw(Point p0, Point p1, Point p2)
{
	Vector a = p1 - p0;
	Vector b = p2 - p0;

	if (cross(a, b) > ESP) return 1;
	if (cross(a, b) < -ESP) return -1;
	if (dot(a, b) < -ESP) return 2;
	if (a.norm() < b.norm()) return -2;
	return 0;
}

bool intersect(Segment a, Segment b)
{
	Point p1, p2, p3, p4;
	p1 = a.p1, p2 = a.p2;
	p3 = b.p1, p4 = b.p2;

	return (ccw(p1, p2, p3) * ccw(p1, p2, p4) <= 0 && ccw(p3, p4, p1) * ccw(p3, p4, p2) <= 0);
}

double Distance_LP(Line l, Point p)
{
	return fabs(cross(l.p2 - l.p1, p - l.p1) / (l.p2 - l.p1).abs());
}

double Distance_SP(Segment s, Point p)
{
	if (dot(s.p1 - s.p2, p - s.p2) < 0.0) return (p - s.p2).abs();
	if (dot(s.p2 - s.p1, p - s.p1) < 0.0) return (p - s.p1).abs();
	return Distance_LP(s, p);
}

double Distance_SS(Segment a, Segment b)
{
	if (intersect(a, b)) return 0.0;
	return min(min(Distance_SP(a, b.p2), Distance_SP(a, b.p1)), min(Distance_SP(b, a.p2), Distance_SP(b, a.p1)));
}

void read(Point& a)
{
	scanf("%lf%lf", &a.x, &a.y);
}

int main()
{
	int q;
	scanf("%d", &q);

	while (q--)
	{
		Point p[4];
		for (int i = 0; i < 4; i++) read(p[i]);

		Segment s1, s2;
		s1.p1 = p[0], s1.p2 = p[1];
		s2.p1 = p[2], s2.p2 = p[3];

		printf("%.10lf\n", Distance_SS(s1, s2));
	}
}

