  while(q--){
    Vec p0, p1, p2, p3;
    p0.read();
    p1.read();
    p2.read();
    p3.read();
    Line l1(p0, p1), l2(p2, p3);
    printf("%.10lf\n", l1.dist(l2));
  }
  return 0;
}

