int Direct2(Point P, Point P1, Point P2) { return sgn((P1-P)/(P2-P)); }

bool IsSegInter(Point P1, Point P2, Point Q1, Point Q2) {
	if (min(P1.x, P2.x) <= max(Q1.x, Q2.x) && max(P1.x, P2.x) >= min(Q1.x, Q2.x) && min(P1.y, P2.y) <= max(Q1.y, Q2.y) && max(P1.y, P2.y) >= min(Q1.y, Q2.y))
		if (Direct2(P1, P2, Q1) * Direct2(P1, P2, Q2) <= 0 && Direct2(Q1, Q2, P1) * Direct2(Q1, Q2, P2) <= 0) return 1;
	return 0;
}

Point GetSegInter(Point P1, Point P2, Point Q1, Point Q2) {
	Point v = P2-P1, w = Q2-Q1; DB t = (Q1-P1)/w / (v/w);
	return P1 + t*v;
}

DB PointLineDist(Point P, Point P1, Point P2) {
	return DB(Proj(P, P1, P2)-P);
}

DB PointSegDist(Point P, Point P1, Point P2) {
	if (Direct1(P1, P2, P) * Direct1(P2, P1, P) >= 0) return PointLineDist(P, P1, P2);
	return min(DB(P-P1), DB(P-P2));
}

DB SegDist(Point P1, Point P2, Point Q1, Point Q2) {
	if (IsSegInter(P1, P2, Q1, Q2)) return 0;
	return min(min(PointSegDist(P1, Q1, Q2), PointSegDist(P2, Q1, Q2)), min(PointSegDist(Q1, P1, P2), PointSegDist(Q2, P1, P2)));
}

