typedef complex<double> P;
struct Line : public vector<P>
{
  Line(P a=P(), P b=P()) {
    push_back(a); push_back(b);
  }
};
typedef Line Segment;

#define EPS (1e-8)

bool operator < (P const& a, P const& b) {
  return (a.real() != a.real()) ? a.real() < b.real() : a.imag() < b.imag();
}

double dot(P const& a, P const& b) {
  return real(conj(a)*b);
}

double cross(P const& a, P const& b) {
  return imag(conj(a)*b);
}

P projection_point(P const& a, P const& b, P const& c) {
  double k = dot(b-a, c-a) / norm(b-a);
  return a+k*(b-a);
}

P reflection_point(P const& a, P const& b, P const& c) {
  return c + 2.*(projection_point(a, b, c) - c);
}

bool is_parallel(Segment const& s, Segment const& t) {
  return abs(cross(s[1]-s[0], t[1]-t[0])) < EPS;
}

bool is_orthogonal(Segment const& s, Segment const& t) {
  return abs(dot(s[1]-s[0], t[1]-t[0])) < EPS;
}

int ccw(P a, P b, P c) {
  b -= a, c -= a;
  if(cross(b, c) > 0) return +1; // counter clockwise
  if(cross(b, c) < 0) return -1;  // clockwise
  if(dot(b, c) < 0) return +2;    // c-a-b
  if(norm(b) < norm(c)) return -2;  // a-b-c
  return 0; // on segment
}

bool is_intersect_ss(Segment const& s, Segment const& t) {
  return ccw(s[0], s[1], t[0]) * ccw(s[0], s[1], t[1]) <= 0
         && ccw(t[0], t[1], s[0]) * ccw(t[0], t[1], s[1]) <= 0;
}

P crosspoint_ss(Segment const& s, Segment const& t) {
  P bline = t[1]-t[0];
  double d1 = abs(cross(bline, s[0]-t[0])), d2 = abs(cross(bline, s[1]-t[0]));
  return s[0] + d1/(d1+d2) * (s[1]-s[0]);
}

bool is_point_onsegment(Segment const& s, P const& p) {
  return abs(s[0]-p)+abs(s[1]-p)-abs(s[1]-s[0]) < EPS;
}

double distance_sp(Segment const& s, P const& p) {
  P q = projection_point(s[0], s[1], p);
  if(is_point_onsegment(s, p)) return abs(p-q);
  return min(abs(s[0]-p), abs(s[1]-p));
}

double distance_ss(Segment const& s, Segment const& t) {
  if(is_intersect_ss(s, t)) return 0.; // 交差は距離 0
  return min(min(distance_sp(t, s[0]), distance_sp(t, s[1])),
             min(distance_sp(s, t[0]), distance_sp(s, t[1])));
}

