Point projection(Line l, Point p)
{
  return l.p1 + (l.p2 - l.p1) * (dot(p - l.p1, l.p2 - l.p1) / (l.p2 - l.p1).norm());
}

Point reflection(Line l, Point p)
{
  Point x = projection(l, p);
  return p + (x - p) * 2;
}

int ccw(Point p0, Point p1, Point p2)
{
  Vector a = p1 - p0, b = p2 - p0;
  if (cross(a, b) > EPS)
  {
    return COUNTER_CLOCKWISE;
  }
  if (cross(a, b) < -EPS)
  {
    return CLOCKWISE;
  }
  if (dot(a, b) < -EPS)
  {
    return ONLINE_BACK;
  }
  if (a.norm() < b.norm())
  {
    return ONLINE_FRONT;
  }
  return ON_SEGMENT;
}

bool is_parallel(Line l1, Line l2)
{
  Vector a = l1.p2 - l1.p1, b = l2.p2 - l2.p1;
  return equals(cross(a, b), 0);
}

bool is_orthogonal(Line l1, Line l2)
{
  Vector a = l1.p2 - l1.p1, b = l2.p2 - l2.p1;
  return equals(dot(a, b), 0);
}

bool intersect(Segment s1, Segment s2)
{
  Point p0 = s1.p1, p1 = s1.p2, p2 = s2.p1, p3 = s2.p2;
  return (ccw(p0, p1, p2) * ccw(p0, p1, p3) <= 0) && (ccw(p2, p3, p0) * ccw(p2, p3, p1) <= 0);
}

Point intersection(Segment s1, Segment s2)
{
  Point p0 = s1.p1, p1 = s1.p2, p2 = s2.p1, p3 = s2.p2;
  double a0 = fabs(cross(p2 - p0, p3 - p0)), a1 = fabs(cross(p2 - p1, p3 - p1));
  return (p0 * a1 + p1 * a0) / (a0 + a1);
}

double distance(Segment s, Point p)
{
  Point p0 = s.p1, p1 = s.p2;
  if (dot(p1 - p0, p - p0) < -EPS)
  {
    return (p0 - p).abs();
  }
  if (dot(p0 - p1, p - p1) < -EPS)
  {
    return (p1 - p).abs();
  }
  return fabs(cross(p1 - p0, p - p0)) / (p1 - p0).abs();
}

double distance(Segment s1, Segment s2)
{
  if (intersect(s1, s2))
  {
    return 0;
  }
  double res = distance(s1, s2.p1);
  res = min(res, distance(s1, s2.p2));
  res = min(res, distance(s2, s1.p1));
  res = min(res, distance(s2, s1.p2));
  return res;
}

