		cin >> p2.x >> p2.y >> p3.x >> p3.y;
		if(segmentIntersectionQ(p0,p1,p2,p3)){
			cout << 0.0 << '\n';
		} else {
			long double ans0 = min(segDist(p0, p1, p2), segDist(p0, p1, p3));
			long double ans1 = min(segDist(p2, p3, p0), segDist(p2, p3, p1));
			cout << min(ans0, ans1) << '\n';
		}
	}
}

#include <iostream>
