    scanf("%d",&q);
    Point p0,p1,p2,p3;
    while(q--){
        p0.input();
        p1.input();
        p2.input();
        p3.input();
        Line line1(p0,p1);
        Line line2(p2,p3);
        double ans = 0;
        if(line1.segcrossseg(line2) == 0){
            ans = line1.dissegtoseg(line2);
        }
        printf("%.10f\n",ans);
    }

    return 0;
}

