	}

};

double dot(point a, point b)
{
	return a.x*b.x + a.y*b.y;
}

double cross(point a, point b)
{
	return a.x*b.y - a.y*b.x;
}

struct line
{
	point a, b;
	line(double x1 = 0, double y1 = 0, double x2 = 0, double y2 = 0) :a(point(x1, y1)), b(point(x2, y2)) {};
	line(point p, point q) :a(p), b(q) {};
	point univec() { return (b - a) / (b - a).norm(); }
};

int ccw(point a, point b, point c)
{
	b = b - a; c = c - a;
	if (cross(b, c) > EPS) return 1;
	if (cross(b, c) < -EPS) return -1;
	if (dot(b, c) < 0) return 2;
	if (b.norm() < c.norm()) return -2;
	return 0;
}

bool isis_sp(line s, point p)
{
	return ((s.a - p).abs() + (s.b - p).abs() - (s.b - s.a).abs()) < EPS;
}

bool isis_ss(line s, line t)
{
	return (ccw(s.a, s.b, t.a)*ccw(s.a, s.b, t.b) <= 0 &&
		ccw(t.a, t.b, s.a)*ccw(t.a, t.b, s.b) <= 0);
}

point cross_point_ss(line s1, line s2)
{
	point base = s2.b - s2.a;
	double d1 = abs(cross(base, s1.a - s2.a));
	double d2 = abs(cross(base, s1.b - s2.a));
	double t = d1 / (d1 + d2);
	return s1.a + (s1.b - s1.a)*t;
}

point projection(line l, point p)
{
	point base = l.b - l.a;
	double r = dot(p - l.a, base) / base.norm();
	return l.a + base*r;
}

double dist_sp(line s, point p)
{
	point r = projection(s, p);
	if (isis_sp(s, r)) return (r - p).abs();
	return min((s.a - p).abs(), (s.b - p).abs());
}

double dist_ss(line s1, line s2)
{
	if (isis_ss(s1, s2)) return 0;
	return min({ dist_sp(s1, s2.a), dist_sp(s1, s2.b),
		dist_sp(s2, s1.a), dist_sp(s2, s1.b) });
}

