class Point {
    public:
    double x, y;

    Point(double x = 0, double y = 0): x(x), y(y) {}

    Point operator + (Point p) { return Point(x+p.x, y+p.y); }
    Point operator - (Point p) { return Point(x-p.x, y-p.y); }
    Point operator * (double a) { return Point(a*x, a*y); }
    Point operator / (double a) { return Point(x/a, y/a); }

    double abs() {return sqrt(norm()); }
    double norm() {return x*x + y*y; }
};

typedef Point Vector;


double dot(Vector a, Vector b) {
    return a.x * b.x + a.y * b.y;
}

double cross(Vector a, Vector b) {
    return a.x*b.y - a.y*b.x;
}

Point projection(Point p1, Point p2, Point p) {
    Vector v = p2 - p1;
    Vector u = p - p1;
    Point t = p1 + v*dot(u,v)/v.norm();
    return t;
}

//2つのベクトルが平行かどうか
bool isParallel(Vector u, Vector v){
    double Cross = cross(u,v);
    bool out = (Cross==0)? true : false;
    return out;
}

//ポイントp2が線分p0p1上にいるかどうか
bool isOn(Point p0, Point p1, Point p2){
    Vector v01 = p1 - p0;
    Vector v02 = p2 - p0;
    Vector v10 = p0 - p1;
    Vector v12 = p2 - p1;

    return cross(v01,v02)==0 && dot(v01,v02)>=0 && dot(v10,v12)>=0;
}

//ポイントp2がベクトルp1-p0からみて反時計回りにいるかどうか
//CCW: 1, CW: -1, ON:0
int isCCW(Point p0, Point p1, Point p2){
    Vector v01 = p1 - p0;
    Vector v02 = p2 - p0;

    double Cross = cross(v01, v02);

    int out = 2;
    if (Cross>0){out = 1;}
    else if(Cross<0){out = -1;}
    else{out = 0;}

    return out;
}

bool isIntersect(Point p0, Point p1, Point p2, Point p3){
    Vector s1 = p1-p0;
    Vector s2 = p3-p2;
    bool out;
    //平行な場合
    if(isParallel(s1,s2))
    {
        out = isOn(p0,p1,p2) || isOn(p0,p1,p3) || isOn(p2,p3,p0) || isOn(p2,p3,p1);
    }
    //平行でない場合
    else     
    {
        out = ( ( isCCW(p0,p1,p2)*isCCW(p0,p1,p3) ) <= 0 ) && ( ( isCCW(p2,p3,p0)*isCCW(p2,p3,p1) ) <= 0 );
    }
    return out;
}


