//pから直線p0p1に下ろした垂線の長さ
double ppd(Point p0, Point p1, Point p){
    Vector v = p1 - p0;
    Vector u = p - p0;

    return fabs(cross(v,u))/v.abs();
}

//pと線分p0p1の距離
double dist(Point p0, Point p1, Point p){
    if ( dot(p1-p0, p-p0) < 0 ) { return (p-p0).abs(); }
    else if ( dot(p0-p1, p-p1) < 0 ){ return (p-p1).abs(); }
    else { return ppd(p0, p1, p); }
}

#include <bits/stdc++.h>
