    Vec operator-(const Vec&o)const{ return Vec(x-o.x,y-o.y); }
    Vec operator*(const double&k)const{ return Vec(k*x,k*y); }
    double operator*(const Vec&o)const {return x*o.x+y*o.y; }
    double operator^(const Vec&o)const {return x*o.y-y*o.x; }
    Vec rotate(double ang){ return Vec(x*cos(ang)-y*sin(ang), x*sin(ang)+y*cos(ang)); }
    Vec change(double l){ if(len()<EPS) return *this; return (*this)*(l/len()); }
    double disToLine(Vec A, Vec B) {
        return abs((A-*this)^(B-*this))/(B-A).len();
    }
    Vec Reflection(Vec A,Vec B){
        Vec P = *this;
        double dis=P.disToLine(A,B);
        int sgn=(((B-A)^(P-A))>=0)?-1:+1;
        return (*this)+(B-A).rotate(acos(-1)/2).change(dis)*sgn;
    }
};

Vec Lineintersect(Vec A1,Vec A2,Vec B1,Vec B2) {
    if (abs((A2-A1)^(B2-B1)) < EPS) return Vec(INF,INF);
    return A1 + (A2 - A1) * ( (((A2-A1)^(B2-B1)) - ((A2-B1)^(B2-B1))) / ((A2-A1)^(B2-B1)) );
}

Vec Segintersect(Vec A1,Vec A2,Vec B1,Vec B2) {
    Vec P = Lineintersect(A1,A2,B1,B2);
    //P.prt();
    if ((P-A1)*(P-A2)<=0 && (P-B1)*(P-B2)<=0) return P; 
    return Vec(INF,INF);
}

bool onseg(Vec P,Vec A,Vec B){
    if(P.disToLine(A,B) > EPS) return 0;
    return (P-A)*(P-B) <= 0;
}
double minDis(Vec P,Vec A,Vec B){
    double ans=min((A-P).len(),(B-P).len());
    Vec H = P.Reflection(A,B);
    if(onseg(H,A,B)) ans=min(ans,(H-P).len());    
    return ans;
}

