const double eps = 1e-10;
using P = complex<double>;
double dot(P a, P b) {return real(conj(a) * b);}
double cross(P a, P b) {return imag(conj(a) * b);}
struct L : vector<P> {L(const P &a = P(), const P &b = P()) {push_back(a); push_back(b);}};
double distLP(L l, P p) {return abs(cross(l[0] - l[1], p - l[0])) / abs(l[1] - l[0]);
    /*return abs(p - projection(l, p));*/}
double distSP(L l, P p) {
    if (dot(l[1] - l[0], p - l[0]) < eps) return abs(l[0] - p);
    if (dot(l[0] - l[1], p - l[1]) < eps) return abs(l[1] - p);
    return distLP(l, p);
}
double norm(P a) {return a.real() * a.real() + a.imag() * a.imag();}
static const int COUNTER_CLOCKWISE = 1;
static const int CLOCKWISE = -1;
static const int ONLINE_BACK = 2;
static const int ONLINE_FRONT = -2;
static const int ON_SEGMENT = 0;
int ccw(P p0, P p1, P p2) {
    P a = p1 - p0;
    P b = p2 - p0;
    if (cross(a, b) > eps) return COUNTER_CLOCKWISE;
    if (cross(a, b) < -eps) return CLOCKWISE;
    if (dot(a, b) < -eps) return ONLINE_BACK;
    if (norm(a) < norm(b)) return ONLINE_FRONT;
    return ON_SEGMENT;
}
bool isec(P p1, P p2, P p3, P p4) {
    return (ccw(p1, p2, p3) * ccw(p1, p2, p4) <= 0 &&
            ccw(p3, p4, p1) * ccw(p3, p4, p2) <= 0);}
bool isec(L s1, L s2) {return isec(s1[0], s1[1], s2[0], s2[1]);}
double distSS(L a, L b) {
    if (isec(a, b)) return 0.0;
    return min({distSP(a, b[0]), distSP(a, b[1]), distSP(b, a[0]), distSP(b, a[1]), distSP(b, a[0])});
}
