{
    return equals(dot(a2 - a1, b2 - b1), 0.0);
}

Point project(Segment s, Point p)
{
    Vector hypo = p - s.p1;
    Vector base = s.p2 - s.p1;
    return base * (dot(hypo, base) / base.norm()) + s.p1;
}

Point reflect(Segment s, Point p)
{
    return (project(s, p) - p) * 2.0 + p;
}

