    }
    point operator *(const double k)const
    {
        point kk;kk.x=k*x;kk.y=k*y;return kk;
    }
    double len(void)
    {
        return sqrt(x*x+y*y);
    }
};

struct line
{
    point s,e;
    line(){}
    line(point _s,point _e)
    {
        s=_s;e=_e;
    }
    pair<int, point> operator &(const line &b)const
    {
        point res=s;
        if(sgn((s-e)^(b.s-b.e))==0){
            if(sgn((s-b.e)^(b.s-b.e))==0){
                return make_pair(0, res);
            }
            else{
                return make_pair(1,res);
            }
        }
        double t=((s-b.s)^(b.s-b.e))/((s-e)^(b.s-b.e));
        res.x+=(e.x-s.x)*t;
        res.y+=(e.y-s.y)*t;
        return make_pair(2, res);
    }
};

bool inter(line l1,line l2)
{
    return max(l1.s.x,l1.e.x) >= min(l2.s.x,l2.e.x) &&
    max(l2.s.x,l2.e.x) >= min(l1.s.x,l1.e.x) &&
    max(l1.s.y,l1.e.y) >= min(l2.s.y,l2.e.y) &&
    max(l2.s.y,l2.e.y) >= min(l1.s.y,l1.e.y) &&
    sgn((l2.s-l1.e)^(l1.s-l1.e))*sgn((l2.e-l1.e)^(l1.s-l1.e)) <= 0 &&
    sgn((l1.s-l2.e)^(l2.s-l2.e))*sgn((l1.e-l2.e)^(l2.s-l2.e)) <= 0;
}
double dis(point x,point y)
{
    return (x-y).len();
}
double lpdis(line l,point p)
{
    return fabs((l.s-l.e)^p-l.s)/(l.s-l.e).len();
}
double dis(line l,point p)
{
    if (sgn((l.e-l.s)*(p-l.s))==-1) {
        return (p-l.s).len();
    }
    if (sgn((l.s-l.e)*(p-l.e)==-1)) {
        return (p-l.e).len();
    }
    return lpdis(l, p);
}
double dis(line l1,line l2)
{
    if (inter(l1, l2)) {
        return 0.0;
    }
    return min(min(dis(l1, l2.s), dis(l1, l2.e)),min(dis(l2, l1.s), dis(l2, l1.e)));
}

