struct OwnVector2
{
	double x, y;
	OwnVector2(double x,double y):x(x),y(y){}
	OwnVector2() :OwnVector2(0, 0) {}
	OwnVector2 operator+(const OwnVector2& v)const
	{
		return OwnVector2(x + v.x, y + v.y);
	}
	OwnVector2 operator-(const OwnVector2& v)const
	{
		return OwnVector2(x - v.x, y - v.y);
	}
	OwnVector2 operator*(const double v)const
	{
		return OwnVector2(x*v, y*v);
	}
	bool operator==(const OwnVector2& v)const
	{
		return abs(x - v.x) <= 0.0000000000001&&abs(y - v.y) <= 0.00000000000001;
	}
	bool operator!=(const OwnVector2& v)const
	{
		return abs(x - v.x) > 0.0000000000001||abs(y - v.y) > 0.00000000000001;
	}

	double dot(const OwnVector2& v)const
	{
		return x*v.x + y*v.y;
	}
	double cross(const OwnVector2& v)const
	{
		return x*v.y - y*v.x;
	}
	double length()const
	{
		return sqrt(x*x + y*y);
	}
	double length2()const
	{
		return x*x + y*y;
	}
};

double CalcMinDistancePointVSSegment(OwnVector2 p1, OwnVector2 p2, OwnVector2 p3)
{
	OwnVector2 p12, p13, p21, p23;
	p12 = p2 - p1; p13 = p3 - p1; p21 = p1 - p2; p23 = p3 - p2;

	if(p12.dot(p13)<0.0)
	{
		return p13.length();
	}

	if (p21.dot(p23) < 0.0)
	{
		return p23.length();
	}

	return abs(p12.cross(p13)) / p12.length();
}

OwnVector2 CalcCrossPoint(OwnVector2 p1, OwnVector2 p2, OwnVector2 p3, OwnVector2 p4)
{
	OwnVector2 p12, p13, p14, p34, p31, p32;
	p12 = p2 - p1; p13 = p3 - p1; p14 = p4 - p1; p34 = p4 - p3; p31 = p1 - p3; p32 = p2 - p3;

	double d, u, v;

	d = p12.cross(p34);

	if (abs(d) <= 0.0000000000001)
	{
		return OwnVector2(INF, INF);
	}

	u = p13.cross(p34) / d;
	v = p13.cross(p12) / d;

	if (u<0.0 || u>1.0)
	{
		return OwnVector2(INF, INF);
	}
	if (v<0.0 || v>1.0)
	{
		return OwnVector2(INF, INF);
	}

	return p1 + p12*u;
}

