		{
			printf("%.10lf\n", 0.0);
		}
		else
		{
			double d0, d1, d2, d3;
			d0 = CalcMinDistancePointVSSegment(p0, p1, p2);
			d1 = CalcMinDistancePointVSSegment(p0, p1, p3);
