#define EPS (1e-10)
struct Point
{
    double x, y;
    Point() {}
    Point(double _x,double _y):x(_x),y(_y) {}
    Point operator +(Point a)
    {
        return Point(x+a.x,y+a.y);
    }
    Point operator -(Point a)
    {
        return Point(x-a.x,y-a.y);
    }

};
double dot(Point a,Point b)
{
    return a.x*b.x+a.y*b.y;
}
double cross(Point a,Point b)
{
    return a.x*b.y-a.y*b.x;
}
double norm(Point a)
{
    return a.x*a.x+a.y*a.y;
}
double abs(Point a)
{
    return sqrt(norm(a));
}

int n;
Point p1,p2,p3,p4;
int ccw(Point p1,Point p2,Point p)
{
    Point a=p2-p1;
    Point b=p-p1;
    if(cross(a,b)<-EPS) return -1;
    if(cross(a,b)>EPS) return 1;
    if(dot(a,b)<-EPS) return -2;
    if(norm(a)<norm(b)) return 2;
    return 0;
}
bool is_insect(Point p1,Point p2,Point p3,Point p4)
{
    if(ccw(p1,p2,p3)*ccw(p1,p2,p4)<=0&&ccw(p3,p4,p1)*ccw(p3,p4,p2)<=0) return true;
    return false;
}
double dist_lp(Point p1,Point p2,Point p)
{
    if(dot(p2-p1,p-p1)<0.0) return abs(p-p1);
    if(dot(p1-p2,p-p2)<0.0) return abs(p-p2);
    return fabs(cross(p2-p1,p-p1))/abs(p2-p1);
}
double dist_ll(Point p1,Point p2,Point p3,Point p4)
{
     if(is_insect(p1,p2,p3,p4)) return 0.0;
    return min(min(dist_lp(p1,p2,p3),dist_lp(p1,p2,p4)),min(dist_lp(p3,p4,p1),dist_lp(p3,p4,p2)));
}
int main()
{
//    freopen("in.txt","r",stdin);
    scanf("%d",&n);
    while(n--)
    {
        scanf("%lf%lf%lf%lf%lf%lf%lf%lf",&p1.x,&p1.y,&p2.x,&p2.y,&p3.x,&p3.y,&p4.x,&p4.y);
        printf("%.10lf\n",dist_ll(p1,p2,p3,p4));
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const double eps = 1e-12;
bool dless(double d1, double d2) { return d1 - d2 <= eps; }
bool dcmp(double d, double d2 = 0) { return abs(d - d2) < eps; }
double sgn(double d) {
  if (dcmp(d))
    return 0;
  if (d > 0)
    return 1;
  return -1;
}

struct vec {
  double x, y;
  vec(double x, double y) : x(x), y(y) {}
  vec() : x(0), y(0) {}
  const bool operator==(const vec &v) const {
    return dcmp(x, v.x) && dcmp(y, v.y);
  }
  const bool operator!=(const vec &v) const { return !(*this == v); }
  const vec operator+(const vec &v) const { return vec(x + v.x, y + v.y); }
  const vec operator-() const { return vec(-x, -y); }
  const vec operator-(const vec &v) const { return *this + (-v); }
  const vec operator*(const double d) const { return vec(x * d, y * d); }
  const vec operator/(const double d) const { return *this * (1 / d); }
  const vec unit() const { return *this / len(); };
  const double len() const { return sqrt(x * x + y * y); }
  const double dot(const vec &v) const { return x * v.x + y * v.y; }
  const double cross(const vec &v) const { return x * v.y - y * v.x; }
  const bool parallel(const vec &v) const { return dcmp(cross(v)); }
  const bool perpendicular(const vec &v) const { return dcmp(dot(v)); }
};

typedef vec point;

template <class T> struct optional {
  bool has;
  T val;
  optional() : has(false) {}
  optional(T t) : has(true), val(t) {}
};

struct line {
  double a, b, c;
  line(point p1, point p2) {
    a = p1.y - p2.y;
    b = p2.x - p1.x;
    c = (p1.x - p2.x) * p1.y + (p2.y - p1.y) * p1.x;
    double m1 = a * p1.x + b * p1.y + c;
    double m2 = a * p2.x + b * p2.y + c;
  }
  bool parallel(line l) { return dcmp(a * l.b, b * l.a); }
  optional<point> intersect(line l) {
    if (parallel(l))
      return optional<point>();
    return point(-(c * l.b - b * l.c) / (a * l.b - b * l.a),
                 (c * l.a - a * l.c) / (a * l.b - b * l.a));
  }
  bool contains(point p) { return dcmp(a * p.x + b * p.y + c); }
  point project(point p) {
    double t = -(a * p.x + b * p.y + c) / (a * a + b * b);
    return point(p.x + a * t, p.y + b * t);
  }
};

struct segment {
  point p1, p2;
  segment(point p1, point p2) : p1(p1), p2(p2) {}
  line sline() { return line(p1, p2); }
  bool contains(point p) {
    double x1 = min(p1.x, p2.x), x2 = max(p1.x, p2.x);
    double y1 = min(p1.y, p2.y), y2 = max(p1.y, p2.y);
    bool ok = sline().contains(p) && (dless(x1, p.x) && dless(p.x, x2)) &&
              (dless(y1, p.y) && dless(p.y, y2));
    return ok;
  }
  bool intersect(segment s) {
    vec v0 = p2 - p1;
    vec v1 = s.p2 - s.p1;
    if (v0.parallel(v1)) {
      return (contains(s.p1) || contains(s.p2)) ||
             (s.contains(p1) || s.contains(p2));
    }
    point a = p1, b = p2, c = s.p1, d = s.p2;
    return sgn((a - b).cross(b - c)) != sgn((a - b).cross(b - d)) &&
           sgn((c - d).cross(d - a)) != sgn((c - d).cross(d - b));
  }
  double distance(point p) {
    point p0 = sline().project(p);
    if (contains(p0)) {
      return (p0 - p).len();
    } else {
      return min((p1 - p).len(), (p2 - p).len());
    }
  }
};

int main() {
  int n;
  cin >> n;
  for (int i = 0; i < n; i++) {
    point p0, p1, p2, p3;
    cin >> p0.x >> p0.y >> p1.x >> p1.y >> p2.x >> p2.y >> p3.x >> p3.y;
    segment l0(p0, p1);
    segment l1(p2, p3);
    double d = 1.0 / 0.0;
    d = min(d, l0.distance(p2));
    d = min(d, l0.distance(p3));
    d = min(d, l1.distance(p0));
    d = min(d, l1.distance(p1));
    if (l0.intersect(l1))
      d = 0;
    printf("%.11lf\n", d);
  }
}

#include <iostream>
#include <math.h>
#include <stdio.h>
#include <vector>

using namespace std;

// 2次元ベクトルクラス
class Vector {
public:
    float x, y;

    // コンストラクタ
    Vector() {
        x = 0.f;
        y = 0.f;
    }

    // コンストラクタ(引数あり)
    Vector(float nx, float ny) {
        x = nx;
        y = ny;
    }

    // x,y成分を設定する
    void set(float nx, float ny) {
        x = nx;
        y = ny;
    }

    // 内積を求める
    float dot(const Vector& vec) const {
        return x * vec.x + y * vec.y;
    }

    // 外積を求める
    float cross(const Vector& vec) const {
        return x * vec.y - y * vec.x;
    }

    // 余弦を求める
    float getCos(const Vector& vec) const {
        float nor = norm() * vec.norm();
        if (nor == 0.f) {
            return 0.f;
        } else {
            return dot(vec) / nor;
        }
    }

    // ベクトルの大きさを求める
    float norm() const {
        return sqrt(x * x + y * y);
    }

    // 単位ベクトルを返す
    Vector getUnit() const {
        Vector v;
        float n = norm();
        if (n == 0.f) {
            v.set(0.f, 0.f);
        } else {
            v.set(x / n, y / n);
        }
        return v;
    }

    void operator = (const Vector& vec) {
        x = vec.x;
        y = vec.y;
    }

    Vector operator + (const Vector& vec) const {
        Vector v(x + vec.x, y + vec.y);
        return v;
    }

    Vector operator - (const Vector& vec) const {
        Vector v(x - vec.x, y - vec.y);
        return v;
    }

    Vector operator * (const Vector& vec) const {
        Vector v(x * vec.x, y * vec.y);
        return v;
    }

    Vector operator * (float f) const {
        Vector v(x * f, y * f);
        return v;
    }

    Vector operator / (const Vector& vec) const {
        Vector v;
        if (vec.x == 0.f) {
            v.x = 0.f;
        } else {
            v.x = x / vec.x;
        }

        if (vec.y == 0.f) {
            v.y = 0.f;
        } else {
            v.y = y / vec.y;
        }
        return v;
    }

    Vector operator / (float f) const {
        Vector v;
        if (f == 0.f) {
            v.set(0.f, 0.f);
        } else {
            v.set(x / f, y / f);
        }
        return v;
    }

    void operator += (const Vector& vec) {
        x += vec.x;
        y += vec.y;
    }

    void operator -= (const Vector& vec) {
        x -= vec.x;
        y -= vec.y;
    }

    void operator *= (const Vector& vec) {
        x *= vec.x;
        y *= vec.y;
    }

    void operator /= (const Vector& vec) {
        if (vec.x == 0.f) {
            x = 0.f;
        } else {
            x /= vec.x;
        }

        if (vec.y == 0.f) {
            y = 0.f;
        } else {
            y /= vec.y;
        }
    }

    bool operator == (const Vector& vec) const {
        if (x == vec.x && y == vec.y) {
            return true;
        } else {
            return false;
        }
    }
};

// ベクトル1をベクトル2に正射影したベクトルを求める
static Vector getProjection(const Vector& vec1, const Vector& vec2) {
    Vector vec(vec2.getUnit() * vec1.norm() * vec1.getCos(vec2));
    return vec;
}


class LineSegment {
private:
    Vector start; // 始点
    Vector end; // 終点

public:
    //コンストラクタ
    LineSegment () {}

    // 始点と終点を設定する
    void setStartEnd(float sx, float sy, float ex, float ey) {
        start.set(sx, sy);
        end.set(ex, ey);
    }

    // 始点を取得する
    Vector getStart() const {
        return start;
    }

    //　終点を取得する
    Vector getEnd() const {
        return end;
    }

    // 指定の線分を含むか
    // このメソッドは線分同士が平行の時しか使用しない
    bool isInLine(const LineSegment& l) const {
        bool b1 = isPointInLine(l.getStart());
        bool b2 = isPointInLine(l.getEnd());
        if (b1 || b2) {
            return true;
        } else {
            return false;
        }
    }

    // 指定の点を線分の中に含むか
    bool isPointInLine(const Vector& p) const {
        if (start == p) {
            return true;
        }

        Vector l1 = end - start;
        Vector l2 = p - start;
        Vector u1 = l1.getUnit();
        Vector u2 = l2.getUnit();

        if (u1 == u2 && l2.norm() <= l1.norm()) {
            return true;
        } else {
            return false;
        }
    }

    // 指定の線分と交差しているか
    // @return 0(交差していない), 1(交差している)
    int isCross(const LineSegment& l) const {
        Vector l1 = end - start;
        float c1 = l1.cross(l.getStart() - start);
        float c2 = l1.cross(l.getEnd() - start);

        Vector l2 = l.getEnd() - l.getStart();
        float c3 = l2.cross(start - l.getStart());
        float c4 = l2.cross(end - l.getStart());

        if (c1 * c2 == 0.f && c3 * c4 == 0.f) { // 平行のとき
            LineSegment ls;
            ls.setStartEnd(start.x, start.y, end.x, end.y);
            if (isInLine(l) || l.isInLine(ls)) {
                return 1;
            }
            return 0;
        } else if ((c1 * c2 < 0.f && c3 * c4 <= 0.f)
                   || (c1 * c2 <= 0.f && c3 * c4 < 0.f)
                   || (c1 * c2 < 0.f && c3 * c4 < 0.f)) { // 交差するとき
            return 1;
        } else { // 交差しないとき
            return 0;
        }
    }

    // 任意の点が線分の始点より外にあるか
    bool isOutOfStart(const Vector& vec) const {
        Vector p = end - start;
        Vector q = vec - start;
        float d = p.dot(q);
        if (d < 0) { // 内積が負のとき
            return true;
        }
        return false;
    }

    // 任意の線分との距離を求める
    float getDistance(const LineSegment& l) const {
        if (isCross(l)) { // 交差しているとき
            return 0.f;
        }
        float dis1 = getDistance(l.getStart());
        float dis2 = getDistance(l.getEnd());
        LineSegment ls;
        ls.setStartEnd(start.x, start.y, end.x, end.y);
        float dis3 = l.getDistance(ls.getStart());
        float dis4 = l.getDistance(ls.getEnd());
        float min1 = min(dis1, dis2);
        float min2 = min(dis3, dis4);
        return min(min1, min2);
    }

    // 任意の点との距離を求める
    float getDistance(const Vector& vec) const {
        if (isPointInLine(vec)) { // 線分内に点を含むとき
            return 0.f;
        }
        if (!isOutOfStart(vec)) { // 線分の始点より内側に点があるとき
            Vector p = end - start;
            Vector q = vec - start;
            Vector proj = getProjection(q, p);
            if (p.norm() > proj.norm()) { // 正射影したベクトルが線分より短いとき
                return (q - proj).norm();
            } else { // 正射影したベクトルが線分と等しいか、長いとき
                return (vec - end).norm();
            }
        } else { // 線分の始点より外にあるとき
            return (vec - start).norm();
        }
    }
};

int main() {
    int n;
    cin >> n;

    LineSegment l1, l2;
    vector<float> result;
    for (int i = 0; i < n; i++) {
        float sx1, sy1, ex1, ey1, sx2, sy2, ex2, ey2;
        cin >> sx1 >> sy1 >> ex1 >> ey1 >> sx2 >> sy2 >> ex2 >> ey2;
        l1.setStartEnd(sx1, sy1, ex1, ey1);
        l2.setStartEnd(sx2, sy2, ex2, ey2);
        result.push_back(l1.getDistance(l2));
    }

    for (int i = 0; i < result.size(); i++) {
        printf("%.8f\n", result[i]);
    }
    return 0;
}

