	double d1=distance(c.o,s.p1),d2=distance(c.o,s.p2);
	return intersect(c,s)?0
		:d1<c.r-EPS&&d2<c.r-EPS?c.r-max(d1,d2)
		:intersect(s,h)?distance(c.o,h)-c.r
		:min(distance(c.o,s.p1),distance(c.o,s.p2))-c.r;
