    Point (double x, double y) : x(x), y(y){}
    Point operator-(Point p){
        return Point(x - p.x, y - p.y);
    }
};
typedef Point Vector;
double dot(Vector v1, Vector v2){
    return v1.x * v2.x + v1.y * v2.y;
}
double abs_(Vector v){
    return sqrt(dot(v, v));
}
double cross(Vector v1, Vector v2){
    return v1.x * v2.y - v1.y * v2.x;
}
struct Segment{
    Point p1;
    Point p2;
    Segment(Point p1, Point p2) : p1(p1), p2(p2){}
};
bool is_intersect(Segment s1, Segment s2){
    double s1_x_min = s1.p1.x;
    double s1_x_max = s1.p2.x;
    if (s1_x_min > s1_x_max) swap(s1_x_min, s1_x_max);
    double s2_x_min = s2.p1.x;
    double s2_x_max = s2.p2.x;
    if (s2_x_min > s2_x_max) swap(s2_x_min, s2_x_max);
    if (s2_x_max < s1_x_min) return false;
    if (s2_x_min > s1_x_max) return false;
    double s1_y_min = s1.p1.y;
    double s1_y_max = s1.p2.y;
    if (s1_y_min > s1_y_max) swap(s1_y_min, s1_y_max);
    double s2_y_min = s2.p1.y;
    double s2_y_max = s2.p2.y;
    if (s2_y_min > s2_y_max) swap(s2_y_min, s2_y_max);
    if (s2_y_max < s1_y_min) return false;
    if (s2_y_min > s1_y_max) return false;
    return true;
}
double get_distanceSP(Segment s, Point p){
// cout << "(" << s.p1.x << "," << s.p1.y << ")";
// cout << "(" << s.p2.x << "," << s.p2.y << ")   :    ";
// cout << "(" << p.x << "," << p.y << ")" << endl;
    Vector a1 = s.p2 - s.p1;
    Vector b1 = p    - s.p1;
    if (dot(a1, b1) <= 0) return abs_(b1);
    Vector a2 = s.p1 - s.p2;
    Vector b2 = p    - s.p2;
    if (dot(a2, b2) <= 0) return abs_(b2);
    return fabs(cross(a1, b1)) / abs_(a1);
}
double get_distanceSS(Segment s1, Segment s2){
    if (is_intersect(s1, s2)) return 0;
    // cout << get_distanceSP(s1, s2.p1) << endl;
    // cout << get_distanceSP(s1, s2.p2) << " ";
    // cout << get_distanceSP(s2, s1.p1) << " ";
    // cout << get_distanceSP(s2, s1.p2) << endl;
    return min(min(get_distanceSP(s1, s2.p1), get_distanceSP(s1, s2.p2)),
               min(get_distanceSP(s2, s1.p1), get_distanceSP(s2, s1.p2)));
}
