}

double getDistanceSP(Segment s, Point p) {
	if (dot(s.p2 - s.p1, p - s.p1) < 0.0) return absv(p - s.p1);
	if (dot(s.p2 - s.p1, p - s.p2) < 0.0) return absv(p - s.p2);
	return getDistance(s, p);
}

double getDistance(Segment s1,Segment s2){
