// 外積
Real cross(const Point& a, const Point& b) {
    return real(a) * imag(b) - imag(a) * real(b);
}

// 内積
Real dot(const Point& a, const Point& b) {
    return real(a) * real(b) + imag(a) * imag(b);
}

// 点の回転方向
//          +1
//          
//  +2   a   0   b  -2
//
//          -1
// http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=CGL_1_C&lang=jp
int ccw(const Point& a, Point b, Point c) {
    b = b - a, c = c - a;
    if (cross(b, c) > EPS) return +1;  // "COUNTER_CLOCKWISE"
    if (cross(b, c) < -EPS) return -1; // "CLOCKWISE"
    if (dot(b, c) < 0) return +2;      // "ONLINE_BACK"
    if (norm(b) < norm(c)) return -2;  // "ONLINE_FRONT"
    return 0;                         // "ON_SEGMENT"
}

// 線分同士の交差判定
// http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=CGL_2_B
bool intersect(const Segment& s, const Segment& t) {
    return ccw(s.a, s.b, t.a) * ccw(s.a, s.b, t.b) <= 0 && ccw(t.a, t.b, s.a) * ccw(t.a, t.b, s.b) <= 0;
}

Real CalcDist(const Segment& s, const Point& p) {
    double t = dot(s.b - s.a, p - s.a) / norm(s.b - s.a);
    Point c = s.a + (s.b - s.a) * t;
    Real res = 1000000.0;
    if (t > -EPS && t < 1.0 + EPS) return sqrt(norm(p - c));
    chmin(res, sqrt(norm(p - s.b)));
    chmin(res, sqrt(norm(p - s.a)));
    return res;
}

int main() {

    int q;
    cin >> q;
    vector<Real> ans(q);

    for (int i = 0; i < q; ++i) {
        Point p0, p1, p2, p3;
        cin >> p0 >> p1 >> p2 >> p3;

        // 交差する場合は0
        if (intersect(Segment(p0, p1), Segment(p2, p3))) ans[i] = 0.0;
        else {
            // 端点だけを見る
            // 点と線分の距離
            Real dist = 1000000.0;
            chmin(dist, CalcDist(Segment(p0, p1), p2));
            chmin(dist, CalcDist(Segment(p0, p1), p3));
            chmin(dist, CalcDist(Segment(p3, p2), p0));
            chmin(dist, CalcDist(Segment(p3, p2), p1));
            ans[i] = dist;
        }
    }

    //for (int i = 0; i < q; ++i) cout << fixed << setprecision(11) << ans[i].real() << " " << ans[i].imag() << endl;
    for (int i = 0; i < q; ++i) cout << fixed << setprecision(11) << ans[i] << endl;

}

