    int CLOCKWISE = -1;
    int ON_SEGMENT = 0;
    int ONLINE_FRONT = 2;
    int ONLINE_BACK = -2;
    Vector a = p1 - p0, b = p2 - p0;
    if(cross(a, b) > EPS) return COUNTER_CLOCKWISE;
    else if(cross(a, b) < -EPS) return CLOCKWISE;
    else if(dot(a, b) < -EPS) return ONLINE_BACK;
    else if(norm(a) < norm(b)) return ONLINE_FRONT;
    else return ON_SEGMENT;
}

double getDistance(Point p1, Point p2, Point p){
    Vector a = p2 - p1, b = p - p1;
