using namespace std;
#define equals(a, b) (fabs((a) - (b)) < EPS)

static const double EPS = 1e-10;
static const int COUNTER_CLOCKWISE = 1;
static const int CLOCKWISE = -1;
static const int ONLINE_BACK = 2;
static const int ONLINE_FRONT = -2;
static const int ON_SEGMENT = 0;

class Point
{
public:
	double x, y;

	Point(double x = 0, double y = 0): x(x), y(y) {}
	Point operator + (Point p) { return Point(x + p.x, y + p.y); }
	Point operator - (Point p) { return Point(x - p.x, y - p.y); }
};

struct Segment
{
	Point p1, p2;
};

typedef Point Vector;
typedef Segment Line;

double
norm(Vector a)
{
	return a.x * a.x + a.y * a.y;
}

double
abs(Vector a)
{
	return sqrt(norm(a));
}

double
dot(Vector a, Vector b)
{
	return a.x * b.x + a.y * b.y;
}


double
cross(Vector a, Vector b)
{
	return a.x * b.y - a.y * b.x;
}

int
ccw(Point p0, Point p1, Point p2)
{
	Vector a = p1 - p0;
	Vector b = p2 - p0;

	if (cross(a, b) > EPS)
		return COUNTER_CLOCKWISE;

	if (cross(a, b) < -EPS)
		return CLOCKWISE;

	if (dot(a, b) < -EPS)
		return ONLINE_BACK;

	if (norm(a) < norm(b))
		return ONLINE_FRONT;

	return ON_SEGMENT;
}

bool
intersect(Point p1, Point p2, Point p3, Point p4)
{
	return (ccw(p1, p2, p3) * ccw(p1, p2, p4) <= 0 &&
	        ccw(p3, p4, p1) * ccw(p3, p4, p2) <= 0);
}

bool
intersect(Segment s1, Segment s2)
{
	return intersect(s1.p1, s1.p2, s2.p1, s2.p2);
}

double
getDistanceLP(Line l, Point p)
{
	return abs(cross(l.p2 - l.p1, p - l.p1) / abs(l.p2 - l.p1));
}

double
getDistanceSP(Segment s, Point p)
{
	if (dot(s.p2 - s.p1, p - s.p1) < 0.0)
		return abs(p - s.p1);

	if (dot(s.p1 - s.p2, p - s.p2) < 0.0)
		return abs(p - s.p2);

	return getDistanceLP(s, p);
}

double
getDistance(Segment s1, Segment s2)
{
	if (intersect(s1, s2))
		return 0.0;

	return min(min(getDistanceSP(s1, s2.p1), getDistanceSP(s1, s2.p2)), min(getDistanceSP(s2, s1.p1), getDistanceSP(s2, s1.p2)));
}

