/* intersect Segment Segment */
bool iSS(L s, L t) {
    return ccw(s[0], s[1], t[0]) * ccw(s[0], s[1], t[1]) <= 0 &&
        ccw(t[0], t[1], s[0]) * ccw(t[0], t[1], s[1]) <= 0;
}

/* intersect Segment Point */
bool iSP(L s, P p) {
    return ccw(s[0], s[1], p) == 0;
}

