D dSP(L s, P p) {
    P r = projection(s, p);
    if(iSP(s, r)) return abs(r - p);
    return min(abs(s[0] - p), abs(s[1] - p));
}

/* distance Segment Segment */
D dSS(L s, L t) {
    if(iSS(s, t)) return 0;
    return min(min(dSP(s, t[0]), dSP(s, t[1])),
            min(dSP(t, s[0]), dSP(t, s[1])));
}

