    template <typename numeric_type>
    bool intersect_segment_segment(const segment<numeric_type> &s, const segment<numeric_type> &t)
    {
        return ccw(s.p, s.q, t.p) * ccw(s.p, s.q, t.q) <= 0 && ccw(t.p, t.q, s.p) * ccw(t.p, t.q, s.q) <= 0;
