    }

    template <typename numeric_type>
    point<numeric_type> projection(const line<numeric_type> &l, const point<numeric_type> &p)
    {
        double t = dot(p - l.p, l.p - l.q) / norm(l.p - l.q);
        return l.p + t * (l.p - l.q);
    }

    template <typename numeric_type, typename float_type = double>
    float_type distance_segment_point(const segment<numeric_type> &s, const point<numeric_type> &p)
    {
        const point<numeric_type> r = projection(s, p);
        if (intersect_segment_point(s, r))
            return abs(r - p);
        return std::min(abs(s.p - p), abs(s.q - p));
    }

    template <typename numeric_type, typename float_type = double>
    float_type distance_segment_segment(const segment<numeric_type> &s, const segment<numeric_type> &t)
    {
        if (intersect_segment_segment(s, t))
            return 0;

        return std::min(std::min(distance_segment_point(s, t.p), distance_segment_point(s, t.q)),
                        std::min(distance_segment_point(t, s.p), distance_segment_point(t, s.q)));
    }

} 

