int ccw(point p0,point p1,point p2){
point a=vec(p1,p0);
point b=vec(p2,p0);
	if(cross(a,b)>eps){
	return 1;//counter_clockwise
	}else if(cross(a,b)<eps){
	return -1;//clockwise
	}else if(dot(a,b)<-eps){
	return 2;//online_back
	}else if(absolute(a)<absolute(b)){
	return -2;//online_front
	}else{
	return 0;//on_segment
	}

}
