    if(v.size() == 2){
        return ccw(v[0],v[1],p)==2;
    }
    int cnt = 0;
    rep(i,v.size()){
        Line m;
        m.PB(v[i]);m.PB(v[(i+1)%v.size()]);
        if(intersectSS(l,m))cnt++;
    }
    return cnt%2;
}

