bool operator<(const P &a, const P &b){
  return a.real() == b.real() ? a.imag() < b.imag() : a.real() < b.real();
}

bool operator==(const P &a, const P &b){
  const double eps = P_eps;
  return abs(a.real() - b.real()) <= eps && abs(a.imag() - b.imag()) <= eps;
}

P rotate(P p, double theta){
