// Vector2
double dot(Vector2 a, Vector2 b){
    return (a.X * b.X + a.Y * b.Y);
}

double cross(Vector2 a, Vector2 b){
    return (a.X * b.Y - a.Y * b.X);
}

double distLP(Line l, Vector2 p){
    return abs(cross(l.second - l.first, p - l.first) / abs(l.second - l.first));
}

// ccw
int ccw(Vector2 p1, Vector2 p2, Vector2 p3){
    Vector2 a = p2 - p1;
    Vector2 b = p3 - p1;
    double dotValue = dot(a, b);
    double crossValue = cross(a, b);
    if(crossValue > EPS) return 1;  // anticlockwise
    if(crossValue < -EPS) return -1;    // clockwise
    if(dotValue < -EPS) return 2;   // p2-p1-p3
    if(norm(a) < norm(b)) return -2;    // p1-p2-p3
    return 0;   // p1-p3-p2
}

// line segment

bool intersect(Segment s1, Segment s2){
    if(ccw(s1.first, s2.first, s2.second) * ccw(s1.second, s2.first, s2.second) > 0){
        return false;
    }
    if(ccw(s2.first, s1.first, s1.second) * ccw(s2.second, s1.first, s1.second) > 0){
        return false;
    }
    return true;
}

double distSP(Segment s, Vector2 p){
    if(dot(s.second - s.first, p - s.first) < 0.0) return abs(p - s.first);
    if(dot(s.first - s.second, p - s.second) < 0.0) return abs(p - s.second);
    return distLP(s, p);
}

double dist(Segment s1, Segment s2){
    if(intersect(s1, s2)){
        return 0.0;
    }
    double a = min(distSP(s1, s2.first), distSP(s1, s2.second));
    double b = min(distSP(s2, s1.first), distSP(s2, s1.second));
    return min(a, b);
}

