    return a.real()*b.imag()-a.imag()*b.real();
}

double getDist(xy_t a,xy_t b,xy_t c){
    //distance between point a and line bc
    xy_t d=c-b,e=a-b,f=a-c;

    if(dot(d,e)<0) return abs(a-b);
    if(dot(-d,f)<0) return abs(a-c);
    else return abs(cross(d,e))/abs(d);

}

