double distSegP(Segment s, Point p) {
	if (dot(p - s.p1, s.p2 - s.p1) > 0 && dot(p - s.p2, s.p1 - s.p2) > 0) {
		return distLineP(s, p);
	}
	else {
		return min(distPP(p, s.p1), distPP(p, s.p2));
	}
}

bool crossPoint(Line l1, Line l2, Point &p) {
	if (isParallel(l1, l2)) {
		return false;
	}
	else {
		double alpha = cross(l2.p1 - l1.p1, l2.p1 - l2.p2) / cross(l1.p2 - l1.p1, l2.p1 - l2.p2);
		p = l1.p1 + (l1.p2 - l1.p1)*alpha;
		return true;
	}
}

bool intersect(Segment s1, Segment s2) {
	Point p;
	if (!crossPoint(s1, s2, p)) {
		return false;
	}
	else {
		if (distPP(s1.p1, s1.p2) < distPP(p, s1.p1) + distPP(p, s1.p2)) {
			return false;
		}
		else {
			return true;
		}
	}
}

double distSegSeg(Segment s1, Segment s2) {
	if (intersect(s1, s2)) {
		return 0.0;
	}
	return min(min(distSegP(s1, s2.p1), distSegP(s1, s2.p2)), min(distSegP(s2, s1.p1), distSegP(s2, s1.p2)));
}

