}
bool is_on_line(point P, line L){
	return abs(ccw(L.A, L.B, P)) != 1;
}
bool is_on_ray(point P, ray L){
  int x = ccw(L.A, L.B, P);
  return x == 0 || x == 2;
}
bool is_on_segment(point P, segment L){
	return ccw(L.A, L.B, P) == 0;
}
