int ccw(P a, P b, P c) {
  b -= a; c -= a;
  if(cross(b, c) > 0) return +1; //ccw
  if(cross(b, c) < 0) return -1; //cw
  if(dot(b, c) < 0)   return +2; //c-a-b on line
  if(norm(b) < norm(c)) return -2; //a-b-c on line
  return 0;
}

P projection(const L& l, const P& p) {
  D t = dot(p - l[0], l[0] - l[1]) / norm(l[0]- l[1]);
  return l[0] + t * (l[0] - l[1]);
}
P refrection(const L& l, const P& p) {
  return p + 2.0 * (projection(l, p) - p);
}

bool intersectSP(const L& l, const P& p) {
  return abs(l[0]-p) + abs(l[1]-p) - abs(l[1] - l[0]) < EPS;
}

bool intersectSS(const L& a, const L& b) {
  return ccw(a[0], a[1], b[0]) * ccw(a[0], a[1], b[1]) <= 0
    && ccw(b[0], b[1], a[0]) * ccw(b[0], b[1], a[1]) <= 0;
}
P crossP(const L& a, const L& b) {
  D x = cross(a[1]-a[0], b[1]-b[0]);
  D y = cross(a[1]-a[0], a[1]-b[0]);
  if(abs(x) < EPS && abs(y) < EPS) return a[0];
  return b[0] + y / x * (b[1]-b[0]);
}
D distSP(const L& l, const P& p) {
  const P r = projection(l, p);
  if(intersectSP(l, r)) return abs(r - p);
  return min(abs(l[0]-p), abs(l[1]-p));
}
D distSS(const L& a, const L& b) {
  if(intersectSS(a, b)) return 0;
  return min({distSP(a, b[0]), distSP(a, b[1]), distSP(b, a[0]), distSP(b, a[1])});
}

