		scanf("%d%d%d%d%d%d%d%d", &px1, &py1, &px2, &py2, &qx1, &qy1, &qx2, &qy2);
		printf("%.9Lf\n", dst(Segment(px1, py1, px2, py2), Segment(qx1, qy1, qx2, qy2)));
	}
	return 0;
}

