Line::Line(double x1, double y1, double x2, double y2) : a(x1, y1), b(x2, y2) {}

Line::Line(const Point &a, const Point &b) : a(a), b(b) {}

double Line::getDistanceTo(const Point &rhs) const {
    const Vector &v1 = b - a;
    const Vector &v2 = rhs - a;
    return fabs(v1.cross(v2) / v1.length());
}

Point Line::getIntersection(const Line &rhs) const {
    if (!intersects(rhs)) {
        return Point();
    }
    Vector u = a - rhs.a;
    Vector v = b - a;
    Vector w = rhs.b - rhs.a;
    double t = w.cross(u) / v.cross(w);
    return a + v * t;
}

Point Line::getProjection(const Point &rhs) const {
    const Vector &v = b - a;
    return a + v * (v.dot(rhs - a) / v.dot(v));
}

Point Line::getReflection(const Point &rhs) const {
    return getProjection(rhs) * 2 - rhs;
}

bool Line::contains(const Point &rhs) const {
    Point p1 = a;
    Point p2 = b;
    if (p1 == rhs) {
        p1 = p1 + (p2 - p1) * 2;
    } else if (p2 == rhs) {
        p2 = p2 + (p1 - p2) * 2;
    }
    return sign((p2 - rhs).cross(p1 - rhs)) == 0;
}

bool Line::intersects(const Line &rhs) const {
    return dcmp((b - a).angle(), (rhs.b - rhs.a).angle()) != 0;
}

Segment::Segment(double x1, double y1, double x2, double y2) : a(x1, y1), b(x2, y2) {}

Segment::Segment(const Point &a, const Point &b) : a(a), b(b) {}

double Segment::length() const {
    return a.getDistanceTo(b);
}

bool Segment::contains(const Point &rhs, bool containsEndPoints) const {
    const Vector &u = a - rhs;
    const Vector &v = b - rhs;
    return sign(u.cross(v)) == 0 && (containsEndPoints ? sign(u.dot(v)) <= 0 : sign(u.dot(v)) < 0);
}

bool Segment::intersects(const Segment &rhs, bool containsEndPoints) const {
    const Vector &u = b - a;
    const Vector &v = rhs.b - rhs.a;
    double c1 = u.cross(rhs.a - a);
    double c2 = u.cross(rhs.b - a);
    double c3 = v.cross(a - rhs.a);
    double c4 = v.cross(b - rhs.a);
    if (containsEndPoints && (!sign(c1) || !sign(c2) || !sign(c3) || !sign(c4))) {
        return contains(rhs.a) || contains(rhs.b) || rhs.contains(a) || rhs.contains(b);
    }
    return sign(c1) * sign(c2) < 0 && sign(c3) * sign(c4) < 0;
}

Point Segment::getIntersection(const Segment &rhs) const {
