double Segment::getDistanceTo(const Point &rhs) const {
    if (a == b) {
        return a.getDistanceTo(rhs);
    }
    const Vector &v1 = b - a;
    const Vector &v2 = rhs - a;
    const Vector &v3 = rhs - b;
    if (sign(v1.dot(v2)) < 0) {
        return v2.length();
    } else if (sign(v1.dot(v3)) > 0) {
        return v3.length();
    } else {
        return rhs.getDistanceTo(Line(a, b));
    }
}

double Segment::getDistanceTo(const Segment &rhs) const {
    if (intersects(rhs)) {
        return 0;
    } else {
        return std::min(std::min(a.getDistanceTo(rhs), b.getDistanceTo(rhs)), std::min(rhs.a.getDistanceTo(*this), rhs.b.getDistanceTo(*this)));
    }
}


