		friend std :: vector<point> line_cross_point(const line &a, const line &b){
			if (parallel(a, b)) return {};
			point u = a.a - b.a, v = a.b - a.a, w = b.b - b.a;
			return {a.a + v * (cross(w, u) / cross(v, w))};
