inline double add(double a, double b){
    return abs(a+b)<(1e-10)*(abs(a)+abs(b)) ? 0.0 : a+b;
}

struct vec{
    double x,y;
    vec operator-(vec b){
        return (vec){add(x,-b.x),add(y,-b.y)};
    }
    vec operator+(vec b){
        return (vec){add(x,b.x),add(y,b.y)};
    }
    vec operator*(double d){
        return (vec){x*d,y*d};
    }
    double dot(vec v){
        return add(x*v.x,y*v.y);
    }
    double cross(vec v){
        return add(x*v.y,-y*v.x);
    }
    double norm(){
        return sqrt(x*x+y*y);
    }
};

const double eps = 0.0000001;


int ccw(vec& a, vec& b, vec& c){
    vec ab = b-a, ac = c-a;
    double o = ab.cross(ac);
    if(o>0) return 1; //CCW
    if(o<0) return -1; //CW
    if(ab.dot(ac)<0){
        return 2; //C-A-B
    }else{
        if(ab.dot(ab)<ac.dot(ac)){
            return -2; //A-B-C
        }else{
            return 0; //A-C-B
        }
    }
}

bool isInterSection(vec (&p)[4]){
    switch(ccw(p[0],p[1],p[2])*ccw(p[0],p[1],p[3])){
        case -1:
        case -4:
        case 0:
            break;
        default:
            return false;
    }
    switch(ccw(p[2],p[3],p[0])*ccw(p[2],p[3],p[1])){
        case -1:
        case -4:
        case 0:
            break;
        default:
            return false;
    }
   return true;
}

vec interSection(vec (&p)[4]){
    vec p23=p[3]-p[2],p02=p[2]-p[0],p01=p[1]-p[0];
    double k = p23.cross(p02)/p23.cross(p01);
    return p[0]+p01*k;
}

double dist(vec (&p)[4]){
    if(isInterSection(p)) return 0;
    double dist =1e5;
    vec P;
    P=p[0]+(p[1]-p[0])*((p[1]-p[0]).dot(p[2]-p[0])/(p[1]-p[0]).dot(p[1]-p[0]));
    if(!ccw(p[0],p[1],P)){
        dist = min(dist,sqrt((P-p[2]).dot(P-p[2])));
    }
    P=p[0]+(p[1]-p[0])*((p[1]-p[0]).dot(p[3]-p[0])/(p[1]-p[0]).dot(p[1]-p[0]));
    if(!ccw(p[0],p[1],P)){
        dist = min(dist,sqrt((P-p[3]).dot(P-p[3])));
    }
    P=p[2]+(p[3]-p[2])*((p[3]-p[2]).dot(p[0]-p[2])/(p[3]-p[2]).dot(p[3]-p[2]));
    if(!ccw(p[2],p[3],P)){
        dist = min(dist,sqrt((P-p[0]).dot(P-p[0])));
    }
    P=p[2]+(p[3]-p[2])*((p[3]-p[2]).dot(p[1]-p[2])/(p[3]-p[2]).dot(p[3]-p[2]));
    if(!ccw(p[2],p[3],P)){
        dist = min(dist,sqrt((P-p[1]).dot(P-p[1])));
    }
    if(dist!=1e5) return dist;
    dist = min(dist,sqrt((p[2]-p[0]).norm()));
    dist = min(dist,sqrt((p[2]-p[1]).norm()));
    dist = min(dist,sqrt((p[3]-p[0]).norm()));
    dist = min(dist,sqrt((p[3]-p[1]).norm()));
    return dist;
}

